import { useState, useEffect} from "react";
import Axios from "axios";
import '../App.css';

export function Createlient() {

    const [name, setName] = useState("");


    const create_client = ()=>{
        Axios.post("http://localhost:3001/create_client",{
          name:name
        }).then(()=>{
          alert("Client Created");
        });
      }

    return (
        <div className="client">
            <div className="input-group flex-nowrap">
                <span className="input-group-text" id="addon-wrapping">Order</span>
                <input onChange={(event)=>{
                setName(event.target.value);
            }} type="text" label="Client Name"/>
            </div>
            <button onClick={create_client}>Save</button>
        </div>
    )
}

export function ListClients() {
    const [clients, setClients] = useState([]);
    useEffect(()=>{
        fetch("http://localhost:3001/list_client").then((data)=>data.json()).then((val=>setClients(val)))
    },[])
      return (
        <div className="input-group flex-nowrap">
            <span className="input-group-text" id="addon-wrapping">Client</span>
            <select  className="form-select" aria-label="client" aria-describedby="addon-wrapping" onChange={(event)=>{
                    setClients(event.target.value);
                }}>
                {
                    clients.map((opts,i)=><option key={i}>{opts.name}</option>)
                }
            </select>
        </div>

      )
}
