import { useState, useEffect} from "react";
import Axios from "axios";
import '../App.css';

export default function GetStatus({statusSeted, changedStatus}) {
    const [status, setStatus] = useState([]);
    const [stautsC, setStatusChanged] = useState(0);

    useEffect(() => {
        fetch("http://localhost:3001/get_status")
            .then((data) => data.json())
            .then((val) => setStatus(val));
    }, []);

    useEffect( ()=> {
        setStatusChanged(changedStatus);
    },changedStatus)

    return (
        <div className="input-group flex-nowrap">
            <span className="input-group-text" id="addon-wrapping">
                Status
            </span>
            <select
                className="form-select"
                aria-label="client"
                aria-describedby="addon-wrapping"
                onChange={(e) => statusSeted(e.target.value)}
                value = {stautsC}
            >
                <option value="">Select a status</option>
                {status.map((opts, i) => (
                    <option key={i} value={opts.id}>
                        {opts.name}
                    </option>
                ))}
            </select>
        </div>
    );
}
