import { useState, useEffect} from "react";
import Axios from "axios";
import {ListClients} from './client';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function CreateOrder() {

    const [order, setOrder] = useState("");
    const [createDate, setCreateDate] = useState("");
    const [client, setClient] = useState(0);
    const [shippingAddress, setShippingAddress] = useState("");
    const [shippingPromise, setShippingPromise] = useState(0);

    const create_order = ()=>{
        Axios.post("http://localhost:3001/create_order",{
          client:client,
          createDate:createDate,
          shippingAddress:shippingAddress,
          shippingPromise:shippingPromise,
        }).then(()=>{
          alert("Order Created");
        });
      }

    return (
        <div class="card text-center">
            <div class="card-header">
                Orders
            </div>
            <div class="card-body">
                <div className="order">
                    <label>Order:<input onChange={(event)=>{
                        setOrder(event.target.value);
                    }} type="text" label="Order id"></input></label>
                    <ListClients/>
                    <label>Create date:<input onChange={(event)=>{
                        setCreateDate(event.target.value);
                    }} type="text" label="Order id"></input></label>
                    <label>Client:<input onChange={(event)=>{
                        setClient(event.target.value);
                    }} type="text" label="Client"></input></label>
                    <label>Shipping Address:<input onChange={(event)=>{
                        setShippingAddress(event.target.value);
                    }} type="text" label="Shipping Address"></input></label>
                    <label>Shipping Promise:<input onChange={(event)=>{
                        setShippingPromise(event.target.value);
                    }} type="date" label="Shipping Promise"></input></label>

                </div>
            </div>
            <div class="card-footer text-body-secondary">
            <button className="btn btn-success" onClick={create_order}>Registrar</button>
            </div>
        </div>

    )

}
