import 'bootstrap/dist/css/bootstrap.min.css';
import { useState, useEffect} from "react";

import {CreateOrder, ListOrders} from './models/orders';
import {CreateClient} from './models/client';
import {CreateItem} from './models/items';

function App() {
  const [isEditing, setIsEditing] = useState(false);
  const [editingOrder, setEditingOrder] = useState(null);
  const [orders, setOrders] = useState([]);

  const toggleEdit = (order) => {
      setIsEditing(!isEditing);
      setEditingOrder(order);
  };


  return (
    <div className="container">
      <div className="App">

        <div>
          <br/>
          <CreateOrder isEditing={isEditing}
                editingOrder={editingOrder}/>
          <br/>
          <ListOrders editOrders={toggleEdit} 
          orders={orders}/>
          <br/>
          <CreateClient/>
          <br/>
          <CreateItem/>
        </div>
      </div>
    </div>
  );
}

export default App;
