import { useState, useEffect} from "react";
import Axios from "axios";
import '../App.css';

export function Createitem() {

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [url, setUrl] = useState("");
    const [price, sePrice] = useState(0);


    const create_item = ()=>{
        Axios.post("http://localhost:3001/create_item",{
          title:title
        }).then(()=>{
          alert("Item Created");
        });
      }

    return (
        <div class="card text-center">
        <div class="card-header">
          Create Item
        </div>
        <div class="card-body">
        <div className="item">
            <div className="input-group flex-nowrap">
                <span className="input-group-text" id="addon-wrapping">Title</span>
                <input onChange={(event)=>{
                setTitle(event.target.value);
                }}  type="text" className="form-control" placeholder="order" aria-label="order" aria-describedby="addon-wrapping"/>
            </div>
            <div className="input-group flex-nowrap">
                <span className="input-group-text" id="addon-wrapping">Description</span>
                <input onChange={(event)=>{
                setDescription(event.target.value);
                }}  type="text" className="form-control" placeholder="Description" aria-label="Description" aria-describedby="addon-wrapping"/>
            </div>
            <div className="input-group flex-nowrap">
                <span className="input-group-text" id="addon-wrapping">URL</span>
                <input onChange={(event)=>{
                setUrl(event.target.value);
                }}  type="text" className="form-control" placeholder="url" aria-label="url" aria-describedby="addon-wrapping"/>
            </div>
            <div className="input-group flex-nowrap">
                <span className="input-group-text" id="addon-wrapping">PRICE</span>
                <input onChange={(event)=>{
                sePrice(event.target.value);
                }}  type="number" className="form-control" placeholder="price" aria-label="price" aria-describedby="addon-wrapping"/>
            </div>
          </div>
        </div>
        <div class="card-footer text-body-secondary">
          <button onClick={create_item}>Save</button>
        </div>
      </div>
    )
}

export function ListClients() {
    const [items, setItems] = useState([]);
    useEffect(()=>{
        fetch("http://localhost:3001/list_item").then((data)=>data.json()).then((val=>setItems(val)))
    },[])
      return (
        <div className="input-group flex-nowrap">
            <span className="input-group-text" id="addon-wrapping">Item</span>
            <select  className="form-select" aria-label="item" aria-describedby="addon-wrapping" onChange={(event)=>{
                    setItems(event.target.value);
                }}>
                {
                    items.map((opts,i)=><option key={i}>{opts.title}</option>)
                }
            </select>
        </div>

      )
}
