import { useState, useEffect} from "react";
import Axios from "axios";
import '../App.css';

export function Createlient() {

    const [name, setName] = useState("");


    const create_item = ()=>{
        Axios.post("http://localhost:3001/create_item",{
          name:name
        }).then(()=>{
          alert("Item Created");
        });
      }

    return (
        <div className="item">
            <label>Item Name:<input onChange={(event)=>{
                setName(event.target.value);
            }} type="text" label="Item Name"></input></label><br/>
            <button onClick={create_item}>Save</button>
        </div>
    )
}

export function ListClients() {
    const [items, setItem] = useState([]);
    useEffect(()=>{
        fetch("http://localhost:3001/list_item").then((data)=>data.json()).then((val=>setItem(val)))
    },[])
      return (
        <div className="input-group flex-nowrap">
            <span className="input-group-text" id="addon-wrapping">Item</span>
            <select  className="form-select" aria-label="order" aria-describedby="addon-wrapping" onChange={(event)=>{
                    setItem(event.target.value);
                }}>
                {
                    items.map((opts,i)=><option key={i}>{opts.name}</option>)
                }
            </select>
        </div>

      )
}
