import { useState, useEffect} from "react";
import Axios from "axios";
import {ListClients} from './client';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function CreateOrder() {

    const [order, setOrder] = useState("");
    const [createDate, setCreateDate] = useState("");
    const [client, setClient] = useState(0);
    const [shippingAddress, setShippingAddress] = useState("");
    const [shippingPromise, setShippingPromise] = useState(0);

    const create_order = ()=>{
        Axios.post("http://localhost:3001/create_order",{
          client:client,
          createDate:createDate,
          shippingAddress:shippingAddress,
          shippingPromise:shippingPromise,
        }).then(()=>{
          alert("Order Created");
        });
      }

    return (
        <div className="card text-center">
            <div className="card-header">
                Create Order
            </div>
            <div className="card-body">
                <div className="order">
                    <div className="input-group flex-nowrap">
                        <span className="input-group-text" id="addon-wrapping">Order</span>
                        <input onChange={(event)=>{
                        setOrder(event.target.value);
                    }} type="text" className="form-control" placeholder="order" aria-label="order" aria-describedby="addon-wrapping"/>
                    </div>
                    <ListClients/>
                    <div className="input-group flex-nowrap">
                        <span className="input-group-text" id="addon-wrapping">Create Date</span>
                        <input onChange={(event)=>{
                            setCreateDate(event.target.value);
                        }} type="date" className="form-date" placeholder="order" aria-label="order" aria-describedby="addon-wrapping"/>
                    </div>
                    <div className="input-group flex-nowrap">
                        <span className="input-group-text" id="addon-wrapping">Shipping Address</span>
                        <input onChange={(event)=>{
                        setShippingAddress(event.target.value);
                        }} type="text" className="form-control" placeholder="Shipping Address" aria-label="Shipping Address" aria-describedby="addon-wrapping"/>
                    </div>
                    <div className="input-group flex-nowrap">
                        <span className="input-group-text" id="addon-wrapping">Shipping date</span>
                        <input onChange={(event)=>{
                        setShippingPromise(event.target.value);
                    }} type="date" className="form-date" placeholder="Shipping date" aria-label="Shipping date" aria-describedby="addon-wrapping"/>
                    </div>
                </div>
            </div>
            <div className="card-footer text-body-secondary">
            <button className="btn btn-success" onClick={create_order}>Registrar</button>
            </div>
        </div>

    )

}
