import { useState, useEffect} from "react";
import Axios from "axios";
import '../App.css';

export default function getStatus() {
    const [getStatus, setStatus] = useState([]);
    useEffect(()=>{
        fetch("http://localhost:3001/get_status").then((data)=>data.json()).then((val=>setStatus(val)))
    },[])
      return (
        <div className="input-group flex-nowrap">
            <span className="input-group-text" id="addon-wrapping">Status</span>
            <select  className="form-select" aria-label="client" aria-describedby="addon-wrapping" onChange={(event)=>{
                    setStatus(event.target.value);
                }}>
                {
                    getStatus.map((opts,i)=><option key={i}>{opts.name}</option>)
                }
            </select>
        </div>

      )
}
